select.if:
select *,(select level from member where idx = board.memberIdx) as level,dateDiff(now(),writeDate) as dateDiff, case when content like '%src="%' then 1 else 0 end as imgCheck from board 
where (nickName like '%${vo.searchString}%' or title like '%${vo.searchString}%' or contentText like '%${vo.searchString}%') 
and part = #{vo.part}  
and good >= 10  
and part = '공지'   
order by idx desc limit #{vo.startIndexNo},#{vo.pageSize}

select *,(select level from member where idx = board.memberIdx) as level,dateDiff(now(),writeDate) as dateDiff, case when content like '%src="%' then 1 else 0 end as imgCheck from board 
where ${vo.searchType} like '%${vo.searchString}%' 
and part = #{vo.part}  
and good >= 10  
and part = '공지'  
order by idx desc limit #{vo.startIndexNo},#{vo.pageSize}
