select.if:
select * from board where (nickName like '%${vo.searchString}%' or title like '%${vo.searchString}%' or contentText like '%${vo.searchString}%') 
and part = #{vo.part}  
and good >= 10  
and part = '공지'   
order by idx desc limit #{vo.startIndexNo},#{vo.pageSize}

select * from board where ${vo.searchType} like '%${vo.searchString}%' 
and part = #{vo.part}  
and good >= 10  
and part = '공지'  
order by idx desc limit #{vo.startIndexNo},#{vo.pageSize}

select count(*) from board where (nickName like '%${vo.searchString}%' or title like '%${vo.searchString}%' or contentText like '%${vo.searchString}%') 
and part = #{vo.part}  
and good >= 10  
and part = '공지'   
order by idx desc limit #{vo.startIndexNo},#{vo.pageSize}

select count(*) from board where ${vo.searchType} like '%${vo.searchString}%' 
and part = #{vo.part}  
and good >= 10  
and part = '공지'  
order by idx desc limit #{vo.startIndexNo},#{vo.pageSize}
